package ConditionalAssignment;

import java.util.Scanner;

public class StarPattern {

	public static void main(String[] args) {
		
		for(int i=1;i<=3;i++)
		{
			for(int j=2;j<4;j++)
			{
			System.out.print("*");
			}
		System.out.println();
		// TODO Auto-generated method stub

	}
	}
}
